// Function to handle the button press even of "Add Game" in "Games" tab
function addGameButtonHandler() {
    var tempCustomGameObj = {};
    var options = {"multiple": false, "title": "Choose the game you want to add", "types": ['exe']};
    settingsWindow.openFileChooserDialog(function (f) {
        if (f.length) {
            console.log(f[0]);
            var filePath = f[0];

            //Code to all the file to our games list
            if (filePath.lastIndexOf(".exe") == -1) {
                tempCustomGameObj.name = filePath.substring(filePath.lastIndexOf("\\") + 1);
            } else {
                tempCustomGameObj.name = filePath.substring((filePath.lastIndexOf("\\") + 1), filePath.lastIndexOf(".exe"));
            }
            tempCustomGameObj.realName = tempCustomGameObj.name;
            tempCustomGameObj.bannerImageFileName = "";
            tempCustomGameObj.gameRunLink = filePath;
            tempCustomGameObj.appID = getLowestAppIDForNewCustomGame();
            tempCustomGameObj.gameBlockHtml = generateBlockContForMain(false, tempCustomGameObj.appID, tempCustomGameObj.gameRunLink, tempCustomGameObj.name, 100000);
            console.log(tempCustomGameObj);
            customGamesList.push(tempCustomGameObj);
            alert(JSON.stringify(tempCustomGameObj));

            preferences.sortedGameList = preferences.sortedGameList.concat(tempCustomGameObj);
            preferences.sortedGameList = sortByKey(preferences.sortedGameList, "name");


            reIndexSortedGamesList();

            populateGamesList();
        }
    }, options);

    document.getElementById(tempCustomGameObj.appID + "-imageBlock").parentNode.scrollIntoView();

}






//Function that will return the next lowest appID value for custom games that can be used while adding a new custom game
function getLowestAppIDForNewCustomGame() {

    var lowest, tmp;

    if (customGamesList.length > 0) {

        lowest = customGamesList[customGamesList.length - 1].appID;
        for (var i = customGamesList.length - 2; i >= 0; i--) {
            tmp = customGamesList[i].appID;
            if (tmp < lowest)
                lowest = tmp;
        }
        return --lowest;
    }
    else {
        return -1;
    }
}






