function saveArtworkChange(imgPath, gameId) {
    var gameImagesPath = Ti.App.getHome() + "\\Resources\\GameBanner\\";


    //Extract path, file name and file extension out of the path
    var fileName = imgPath.split('\\').pop().split('/').pop();
    var fileExt = imgPath.substr(imgPath.lastIndexOf("."));
    var filePath = imgPath.substr(0, (imgPath.lastIndexOf("\\") + 1));



    console.log("cameHere-1");
    //Copy the new file to GameBanner
    var newImageFile = Ti.Filesystem.getFile(filePath, fileName);
    newImageFile.copy(gameImagesPath + gameId + fileExt);
    console.log("cameHere-2");



    //Read the games json object to see if it is set to use a custom artwork
    var fileToDelete;
    gamesList.forEach(function (item) {
        if (item.appID == gameId) {
            var currImgExt = item.bannerImageFileName.substr(item.bannerImageFileName.lastIndexOf("."));

            if (currImgExt != fileExt) {
                console.log("cameHere-2B");
                fileToDelete = Ti.Filesystem.getFile(gameImagesPath, item.bannerImageFileName);
                fileToDelete.deleteFile();
            }

            item.bannerImageFileName = gameId + fileExt;

            //Change the image on the settings window
            $("#" + gameId + "-gamePath").attr("value", imgPath);
            $("#" + gameId + "-imageBlock").css("background-image", "url(./GameBanner/" + item.bannerImageFileName + "?rngesus=" + Math.random() + ")");

            return false;
        }
    });
}









