//Function to sort an array of objects with a particular keys
function sortByKey(array, key) {
    return array.sort(function (a, b) {
        var x = a[key].toLowerCase();
        var y = b[key].toLowerCase();
        return ((x < y) ? -1 : ((x > y) ? 1 : 0));
    });
}